import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';
function Testimonials() {
const reviews = [{
    name: "Mikiyas Alemu",
    stars: 5,
    text: "The Double Burger is absolutely phenomenal! Juicy, flavorful, and cooked to perfection. This is now my go to burger spot in Addis!",
    date: "February 15, 2025",
    img: "data:image/jpeg;base64,"  
},
{
    name: "Samrawit Birhanu",
    stars: 4.5,
    text: "Best burgers in Bole! The fries are crispy, the service is friendly, and the atmosphere is great. My family loves coming here every Friday.",
    date: "January 28, 2025",
    img: "data:image/jpeg;base64,"
},
{
    name: "Daniel Yared",
    stars: 5,
    text: "I've tried burgers all over the city, and Big Burger's Special is hands down the best. The combination of flavors is just incredible!",
    date: "March 3, 2025",
    img: "data:image/jpeg;base64,"
},
{
    name: "Marta H/Micheal",
    stars: 4,
    text: "The Chicken Burger is my absolute favorite! Always fresh and delicious. The delivery is always on time too. Highly recommended!",
    date: "February 22, 2025",
    img:"data:image/png;base64,"
},
{
    name: "Hayelom Goitom",
    stars: 5,
    text: "As a burger enthusiast, I can confidently say Big Burger lives up to the hype. Quality ingredients and perfect cooking every time.",
    date: "February 10, 2025",
    img: "data:image/jpeg;base64,"
},
{
    name: "Liya Kibru",
    stars: 4.5,
    text: "The Cheese Burger with extra pickles is my comfort food. Perfect after a long day at work. Thank you for always making it right!",
    date: "March 8, 2025",
    img: "data:image/jpeg;base64,"
},
{
    name: "Yosef Teklu",
    stars: 5,
    text: "Been coming here for months now. Consistent quality, great service, and fair prices. The beef burger is always perfectly cooked.",
    date: "January 15, 2025",
    img: "data:image/jpeg;base64,"
},
{
    name: "Meron Geremew",
    stars: 4,
    text: "Great food and excellent value for money. The fries are addictive! Will definitely be returning with friends soon.",
    date: "February 5, 2025",
    img: "data:image/jpeg;base64,"
},
{
    name: "Dawit Dagne",
    stars: 5,
    text: "Ordered through Yegna Delivery and everything arrived hot and fresh. The packaging was excellent too. Five stars for service and food!",
    date: "March 12, 2025",
    img: "data:image/jpeg;base64,"
},
{
    name: "Leul Mesfin",
    stars: 4.5,
    text: "The Big Burger Special is worth every birr! Generous portions and amazing taste. This place has become our family's favorite weekend treat.",
    date: "February 28, 2025",
    img: "data:image/png;base64,"
}
];
const renderStars = (rating) => {
const stars = [];
for (let i = 1; i <= 5; i++) {
    if (i <= rating) {
    stars.push(<i key={i} className="fas fa-star"></i>);
    } else if (i - 0.5 === rating) {
    stars.push(<i key={i} className="fas fa-star-half-alt"></i>);
    } else {
    stars.push(<i key={i} className="far fa-star"></i>);
    }
}
    return stars;
};
  return (
    <div className="testimonialsPageWrapper">
      <header>
        <nav>
          <div className="navLeft">
            <img src="https://i.ibb.co/Q7H3fyx5/logo.png" alt="Big Burger Logo" className="homepageLogo" />
          </div>
          <div className="navRight">
          <Link to="/" className="navButton">Home</Link>
          <Link to="/menu" className="navButton">Menu</Link>
          <Link to="/about" className="navButton">About</Link>
          <Link to="/contact" className="navButton">Contact</Link>
          <Link to="/testimonials" className="navButton">Testimonials</Link>
          </div>
        </nav>
      </header>
      <main id="content">
        <h1 className="testimonialsTitle">Customer Testimonials</h1>
        <p className="testimonialsSubtitle">See what our customers are saying about Big Burger</p>
        <div className="testimonialsContainer">
          {reviews.map((review, index) => (
            <div className="testimonialCard" key={index}>
              <div className="testimonialHeader">
                <img src={review.img} alt={review.name} className="testimonialImg" />
                <div className="testimonialInfo">
                  <h3 className="testimonialName">{review.name}</h3>
                  <div className="starRating">
                    {renderStars(review.stars)}
                  </div>
                </div>
              </div>
              <p className="testimonialText">"{review.text}"</p>
              <p className="testimonialDate">{review.date}</p>
            </div>
          ))}
        </div>
        <div className="overallRating">
          <h2>Overall Rating: <span className="ratingNumber">4.7</span>/5</h2>
          <div className="overallStars">
            <i className="fas fa-star"></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star-half-alt"></i>
          </div>
          <p className="ratingCount">Based on customer reviews</p>
        </div>
        <div className="addTestimonial">
          <h2>Share Your Experience</h2>
          <p>Enjoyed your meal at Big Burger? We'd love to hear from you</p>
          <button className="shareButton">Leave a Review</button>
        </div>
      </main>
      <footer className="footer">
        <div className="footerInner">
          <div className="footerBrand">
            <img src="https://i.ibb.co/Q7H3fyx5/logo.png" alt="Big Burger Icon" className="footerLogo"/>
            <p className="footerSlogan"> Real burgers, real flavor</p>
          </div>
          <div className="footerContact">
            <h4>Contact Information</h4>
            <p>
              <strong>Big Burger HQ</strong><br />
              Bole Atlas, Addis Ababa, Ethiopia
            </p>
            <span>Phone: +251 911 055 508</span><br/>
            <span>WhatsApp: +251 935 287 463</span><br/>
            <span>Email: order@bigburger.et</span><br/>
          </div>
          <div className="footerHours">
            <h4>Business Hours</h4>
            <ul>
              <li>Mon - Fri: 11:00 AM - 10:00 PM</li>
              <li>Sat - Sun: 10:00 AM - 11:00 PM</li>
            </ul>
          </div>
          <div className="footerLinks">
            <h4>Quick Links</h4>
            <Link to="/">Home</Link>
          <Link to="/menu">Menu</Link>
          <Link to="/about">About</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/testimonials" >Testimonials</Link>
          </div>
        </div>
        <div className="footerLegal">
          <p>&copy; 2025 Basliel Sisay. All rights reserved</p>
          <div className="legalLinks">
            <span>Privacy Policy</span>
            <span>Terms of Service</span>
            <span>Cookie Policy</span>
          </div>
        </div>
      </footer>
    </div>
  );
}
export default Testimonials;